  font-size: 0.9em;
}
.block-event-quote .card-body {
  background: #f1f2f3;
}
.block-event-quote blockquote p {
  display: inline;
}
.block-event-quote blockquote:before,
.block-event-quote blockquote:after {
  font: normal normal normal 16px/1 FontAwesome;
  display: inline-block;
  opacity: 0.5;
  vertical-align: middle;
}
.block-event-quote blockquote:before {
  content: "\f10d";
