  }
}
@-moz-keyframes flip-card-out {
  0% {
    transform: rotateY(0deg);
    opacity: 1;
  }
  49% {
    opacity: 1;
  }
  50% {
    opacity: 0;
  }
  100% {
    transform: rotateY(180deg);
    opacity: 0;
  }
}
@-webkit-keyframes flip-card-out {
