css:
.block-gallery .thumbs {
  display: flex;
  flex-wrap: wrap;
  align-items: center;
}
.block-gallery .thumb {
  flex: 1 1 auto;
  min-width: 140px;
  width: 20%;
  background-color: #eee;
  margin: 1px;
}
.block-gallery .thumb .inner {
  width: 100%;
  padding-bottom: 75%;
  height: 0;
}
.modal-block-gallery .modal-dialog {
  max-width: 90vw;
}
.modal-block-gallery .modal-content {
  background: none;
  border: 0;
}
.modal-block-gallery .modal-content .inner {
  width: 100%;
  height: 90vh;
  background-repeat: no-repeat;
  background-size: contain;
  background-position: center center;
}

