css:
.block-image-compare .container {
  width: 100%;
  height: 400px;
  position: relative;
  overflow: hidden;
}
.block-image-compare .container .thumb {
  width: 50%;
  height: 100%;
  top: 0;
  left: 0;
  position: absolute;
  background: #eee;
  background-repeat: no-repeat;
  background-position: left center;
}
.block-image-compare .container .thumb:nth-child(2) {
  background-color: #ddd;
  background-position: right center;
  right: 0;
  left: auto;
}
.block-image-compare .container .ctrl {
  height: 100%;
  width: 4px;
  background: #fff;
  position: absolute;
  left: 50%;
  transform: translate(-50%, 0);
  cursor: pointer;
}
.block-image-compare .container .ctrl:after {
  content: "\f07e";
  display: block;
  font: normal normal normal 15px FontAwesome;
  width: 32px;
  height: 32px;
  color: #fff;
  background: #ccc;
  border: 4px solid #fff;
  border-radius: 50%;
  padding: 5px;
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
}

