css:
.block-landing-left {
  background-color: #444;
  background-image: url("/assets/img/sample/t2.png");
  color: #fff;
  width: 100%;
  height: 100vh;
  max-height: 640px;
}
.block-landing-left h1 {
  color: #fff;
}
.block-landing-left .row {
  max-width: 900px;
  margin: auto;
}
.block-landing-left > .inner {
  width: 100% !important;
  max-width: initial !important;
  margin: 0 !important;
  height: 100%;
  display: table;
}
.block-landing-left > .inner > .inner {
  margin: 0;
  padding: 40px 20px;
  display: table-cell;
  vertical-align: middle;
  text-align: left;
  background-repeat: none;
  background-position: center center;
  background-size: cover;
}
.block-landing-left .thumb {
  width: 100%;
  height: 100%;
  min-height: 180px;
}
@media (max-width: 640px) {
  .block-landing-left .col-sm:nth-of-type(2) {
    display: none;
  }
}
