css:
.block-landing {
  background: #444;
  color: #fff;
  width: 100%;
  height: 100vh;
  max-height: 640px;
}
.block-landing h1 {
  color: #fff;
}
.block-landing > .inner {
  width: 100% !important;
  max-width: initial !important;
  margin: 0 !important;
  height: 100%;
  display: table;
}
.block-landing > .inner > .inner {
  margin: 0;
  padding: 40px 20px;
  display: table-cell;
  vertical-align: middle;
  text-align: center;
  background-repeat: none;
  background-position: center center;
  background-size: cover;
}
