css:
.block-questionnaire .row {
  width: 90%;
}
.block-questionnaire .submit {
  min-width: 200px;
  margin-left: 0;
}
.block-questionnaire .result {
  display: none;
  text-align: right;
  margin: 0 0 0 auto;
  border-radius: 5px;
  padding: 0 20px;
}
.block-questionnaire .score {
  color: #d21;
  font-size: 3em;
}
.questions {
  padding-left: 1em;
  position: relative;
}
.questions .question.repeat-ghost {
  width: 100%;
}
.questions .question {
  display: list-item;
  margin: 0 auto 30px 0;
  width: 90%;
  list-style-type: decimal;
}
.questions .question p {
  margin-bottom: 5px;
}
.questions .question .choices {
  display: flex;
  flex-wrap: wrap;
}
.questions .question .choices .choice {
  cursor: pointer;
  padding: 5px 10px;
  flex: 1 1 40%;
  text-align: left;
}
.questions .question .choices .choice:before {
  content: "\f1db";
  font: normal normal normal 14px/1 FontAwesome;
  display: inline-block;
  vertical-align: middle;
  margin: -1px 10px 0 0;
  font-size: 1.2em;
}
.questions .question .choices .choice.active:before {
  content: "\f058";
}

js:
// Generated by LiveScript 1.3.0
module.exports = {
  wrap: function(node){
    var scoring;
    node.querySelector('.result').style.display = 'none';
    node.querySelector('.submit').addEventListener('click', function(){
      return node.querySelector('.result').style.display = 'block';
    });
    scoring = function(){
      var result;
      result = Array.from(node.querySelectorAll('.choice.active')).map(function(it){
        return +(it.getAttribute('score') || 0);
      }).reduce(function(a, b){
        return a + b;
      }, 0);
      node.querySelector('.score').innerText = result;
      return node.querySelector('.result').style.display = 'none';
    };
    return node.addEventListener('click', function(e){
      var target;
      target = e.target;
      if (!target.classList.contains('choice')) {
        return;
      }
      Array.from(target.parentNode.childNodes).map(function(it){
        if (it !== target && it.classList.contains('choice')) {
          return it.classList.remove('active');
        }
      });
      target.classList.toggle('active');
      return scoring();
    });
  }
};
