css:
.block-slides {
  padding: 0;
  margin: 0;
}
.block-slides > .inner {
  padding: 0;
  margin: 0 !important;
  width: 100% !important;
  max-width: initial !important;
}
.block-slides .slides {
  width: 100%;
  min-height: 100vh;
  position: relative;
  overflow: hidden;
}
.block-slides .slides > .fa {
  position: absolute;
  z-index: 10;
  cursor: pointer;
  top: 50%;
  transform: translate(0, -50%);
  font-size: 3em;
  color: #fff;
  text-shadow: 0 0 5px rgba(0,0,0,0.2);
}
.block-slides .slides > .fa-chevron-right {
  right: 10px;
}
.block-slides .slides > .fa-chevron-left {
  left: 10px;
}
.block-slides .slide {
  width: 100%;
  height: 100vh;
  overflow: hidden;
  position: absolute;
  display: table;
  left: 0;
  top: 0;
  z-index: 0;
  opacity: 0;
}
.block-slides .slide > .inner {
  display: table-cell;
  width: 100%;
  height: 100vh;
  text-align: center;
  vertical-align: middle;
}

